import React from 'react';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Slider.scss'
import App from '../App';

const ImageSlider =(props)=> {
    let settings = {
        adaptiveheight: true,
        
        dots: true,
        autoplay: true,
        draggable: true,
        infinite: true,
        speed: 1000,
        slidesToShow: 9,
        slidesToScroll: 1,
        cssEase: "linear"
    }
    return (
        <Slider {...settings}>
           
            <div className="card-wrapper">
                <div className="card">
                    <div className="card-image">
                        <img src="images/9.jpg" />
                    </div>
                    <div className="details">
                        <h2>{props.name} <span className="job-title">{props.id}</span></h2>
                    </div>
                </div>
            </div>

        </Slider>
    )
}

export default ImageSlider
