import React, {Component} from 'react';
import Navbar from './components/Navbar';
import './App.css';
import Main from './components/Main-container';
import Main2 from './components/Main-container';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import ImageSlider from './components/Slider';
import Hero from './components/Hero-container';
import { imgdata } from './components/img-data';
import ArticleContainer from './components/Article-container';
import ImageSlider2 from './components/Slider2';

class App extends Component{

  constructor(){
    super();
    this.state={
      books:[],
      books2:[]
    }

  }

  componentDidMount(){
    fetch('./data.json')
    .then(response=>response.json())
    .then(user =>this.setState({books: user}));
  }
  componentDidMount(){
    fetch('./data2.json')
    .then(response=>response.json())
    .then(user =>this.setState({books2: user}));
  }
  render(){return (
    <>
      <Router>
        <Navbar />
        <Main></Main>
        {/* <div className='gg'>
        <ImageSlider>
        {this.state.books.map(book=><p key={book.id}>{book.title} </p>)}
        </ImageSlider> </div> */}
       <ArticleContainer></ArticleContainer>

       <ImageSlider>
        {this.state.books2.map(book=><p key={book.id}>{book.title} </p>)}
        </ImageSlider>

      </Router>
    </>
  );
}}

export default App;
